sysauto_script.condition:
var rightNow = new GlideDateTime();
var currentDay = rightNow.getDayOfWeekLocalTime();
var isWeekday = false;
if (currentDay != 6 && currentDay != 7) {
	isWeekday = true;
}
else {
	isWeekday = false;
}

isWeekday;

sysauto_script.script:
var dueSoonTasks = new GlideRecord('x_58872_needit_needit_task');
dueSoonTasks.addQuery('due_date','>=',rightNow);
dueSoonTasks.addQuery('due_date','<',gs.hoursAgo(-24));
dueSoonTasks.addQuery('state','!=',3);
dueSoonTasks.query();
while(dueSoonTasks.next()){
	gs.eventQueue('x_58872_needit.dueSoonNeedItTask',dueSoonTasks,dueSoonTasks.number,gs.getUserName(),'my_queue');
	gs.info('---> Find dueSoonNeedIt Task Records Expiry in 24Hours \t NeedIt Task:' +dueSoonTasks.getValue('number') );
}
